INSERT INTO `oce_story` VALUES ('14', '引领通信业的新浪潮', 'yin-ling-tong-xin-ye-de-xin-lang-chao', '<section powered-by=\"xiumi.us\"><section><section><section><p style=\"text-align: left;\">&nbsp; &nbsp;武汉六博光电技术有限责任公司由毕业于武汉大学的六位博士创建，公司主要技术和研发人员来源于武汉大学激光通信实验室该实验室研制的天宫二号激光通信载荷在2016年12月成功地与新疆南山光学地面站进行了1.6Gbps速率的高速激光通信试验，创我国最高卫星通信速率纪录，目前仍在轨运行。在移动激光通信技术方面位居国内前茅。公司执行董事艾勇教授长期从事空间激光通信技术研究，带领的研究团队在空间激光通信方面取得多项国内领先水平的科研成果。<br style=\"text-align: left;\"/></p></section></section></section></section><section style=\"margin: 0px; padding: 0px; color: rgb(62, 62, 62); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; white-space: normal; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;\" powered-by=\"xiumi.us\"><section style=\"margin: 0px; padding: 0px; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;\"><section style=\"margin: 0px; padding: 0px; text-align: justify; color: rgb(95, 156, 239); -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;\"><p style=\"padding: 0px; clear: both; margin-top: 0px; margin-bottom: 0px; -ms-word-wrap: break-word !important; min-height: 1em; max-width: 100%; box-sizing: border-box;\"><br style=\"text-align: left;\"/></p></section></section></section><section style=\"margin: 0px; padding: 0px; color: rgb(62, 62, 62); font-family: &quot;Helvetica Neue&quot;, Helvetica, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; white-space: normal; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;\" powered-by=\"xiumi.us\"><section style=\"margin: 10px 0px; padding: 0px 6px 6px 0px; text-align: center; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box;\"><section style=\"margin: 0px; padding: 6px 0px 0px; display: inline-block; -ms-word-wrap: break-word !important; max-width: 100%; box-sizing: border-box; background-c